#include <iostream>
#include <string>

using namespace std;

class Solution
{
public:
    int findUniqChar( string s )
    {
        unsigned int i;
        int pos;
        int cnt[26] = { 0 };
        for( i = 0; i < s.length(); i++ )
        {
            pos = s[ i ] - 'a';
            cnt[ pos ]++;
        }

        for( i = 0; i < s.length(); i++ )
        {
            pos = s[ i ] - 'a';
            if( cnt[ pos ] == 1 )
                return i;
        }
        return -1;
    }
};

int main( int argc, char* argv[] )
{
    string str( "" );

    Solution s;
    cout << s.findUniqChar( str ) << endl;
    return 0;
}